import PhonemeDropDown from "./PhonemeDropDown";
import { useState } from "react";
import { request } from "../helpers/request";

const requestSearch = async (s) => {
  let quotes = 0;
  let alarm = false;
  for (let i = 0; i < s.length; i++) {
    if (s[i] === "(") quotes++;
    else if (s[i] === ")" && quotes > 0) quotes--;
    else if (s[i] === ")") alarm = true;
  }
  if (!alarm && quotes === 0) {
    // return HTML of table
    const test = await request(s, "search");
  } else {
    // set off alarm
    alert("Search syntax error");
    return false;
  }
};

function Search() {
  const [search, setSearch] = useState("");
  const addToSearch = (IPAChar) => setSearch(search + IPAChar);
  return (
    <span className="container right">
      <h1 className="main-title">Rhyme Search</h1>
      <h5 className="sub-title">A linguistic search engine by Xander</h5>
      <div className="toolbar">
        <span className="phone-drop">
          <PhonemeDropDown addToSearch={addToSearch}></PhonemeDropDown>
        </span>
        <a href="google.com" target="_blank" className="help-link">
          Need help?
        </a>
      </div>
      <div className="content-box search-input-box">
        <input
          type="text"
          className="search-input"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          onKeyDown={(e) => {
            if (e.keyCode === 13) {
              requestSearch(search);
            }
          }}
        ></input>
        <i
          className="fa fa-search"
          style={{
            fontSize: "1.5em",
            cursor: "pointer",
          }}
          onClick={() => {
            requestSearch(search);
          }}
        ></i>
      </div>
      <div className="content-box search-results">
        <div className="table-wrap">
          <table>
            <tbody>
              <tr>
                <td>
                  Sed ut perspiciatis, unde omnis iste natus error sit
                  voluptatem accusantium doloremque laudantium
                </td>
                <td>
                  sɛd ət pɹ̩spɪʃiətɪs, ʌnd ɑmnɪs ɪst nætəs ɛɹɹ̩ sɪt vowlʌptətəm
                  əkjuzæntiəm dowlɹ̩ɪmk lɔdæntiəm
                </td>
              </tr>
              <tr>
                <td>
                  totam rem aperiam eaque ipsa, quae ab illo inventore veritatis
                  et quasi architecto beatae vitae dicta sunt, explicabo
                </td>
                <td>
                  towtæm ɹɛm ejpɛɹiəm ik ɪpsə, kwɑ æb ɪlow ɪnvɛntɔɹi vɛɹɪtətɪs
                  ɛt kwɑsi ɑɹkətɛktow bt vɪt dɪktə sʌnt, ɪksplɪkɑbow
                </td>
              </tr>
              <tr>
                <td>
                  Nemo enim ipsam voluptatem, quia voluptas sit, aspernatur aut
                  odit aut fugit, sed quia consequuntur magni dolores eos
                </td>
                <td>
                  nɛmow ɛnɪm ɪpsəm vowlʌptətəm, kwiə vowlʌptəs sɪt, æspɹ̩nætɹ̩ ɔt
                  ɑdɪt ɔt fjud͡ʒɪt, sɛd kwiə kənsəkwʌntɹ̩ mæɡni dəlɔɹɪs iɔs
                </td>
              </tr>
            </tbody>
          </table>
        </div>
      </div>
    </span>
  );
}

export default Search;
