import Box from "@mui/material/Box";
import { TreeViewBaseItem } from "@mui/x-tree-view/models";
import { RichTreeView } from "@mui/x-tree-view/RichTreeView";
import PHONEME_SCHEMA from "./phonemeSchema";

const treeTag = () => {
  return <div onClick={(e) => {}}>yea</div>;
};

function PhonemeDropDown() {
  console.log(PHONEME_SCHEMA);
  const usedIDs = [];
  const generateID = (ID) => {
    if (usedIDs.includes(ID)) {
      ID = ID + "i";
    }
    usedIDs.append(ID);
    return ID;
  };
  const generatePhonemeItems = (PHS) => {
    if ("c" in PHS) {
      PHS.c.map((cc) => {
        return {
          id: generateID(cc),
          label: cc,
          children: generatePhonemeItems(cc),
        };
      });
    } else {
      PHS.map((cc) => {
        return { id: generateID(cc), label: cc };
      });
    }
  };
  return (
    <Box
      sx={{
        minHeight: 33,
        flexGrow: 1,
        backgroundColor: "white",
        boxShadow: "5px 5px 10px gray",
        borderRadius: "5px",
        overflowY: "scroll",
        overflow: "auto",
        maxHeight: "65%",
      }}
      className="phone-drop"
    >
      <RichTreeView items={generatePhonemeItems(PHONEME_SCHEMA)} />
    </Box>
  );
}

export default PhonemeDropDown;
