import Box from "@mui/material/Box";
import { TreeViewBaseItem } from "@mui/x-tree-view/models";
import { RichTreeView } from "@mui/x-tree-view/RichTreeView";
import { SimpleTreeView } from "@mui/x-tree-view/SimpleTreeView";
import { TreeItem } from "@mui/x-tree-view/TreeItem";
import PHONEME_SCHEMA from "./phonemeSchema";

function PhonemeDropDown({ addToSearch }) {
  // vvvvv redo to generate components
  const generatePhonemeItems = (PHS) => {
    if (typeof PHS === "object") {
      // wrap children in TreeItem
      return {
        id: PHS.id,
        label: PHS.id,
        children: PHS.c.map((cc) => {
          return generatePhonemeItems(cc);
        }),
      };
    } else {
      return { id: PHS, label: PHS };
    }
  };
  const generatePhonemeItems2 = (PHS) => {
    if (typeof PHS === "object") {
      return (
        <TreeItem itemId={PHS.id} label={PHS.id}>
          {PHS.c.map((cc) => {
            return generatePhonemeItems2(cc);
          })}
        </TreeItem>
      );
    } else {
      return <TreeItem itemId={PHS.id} label={PHS.id}></TreeItem>;
    }
  };
  console.log(generatePhonemeItems2(PHONEME_SCHEMA));
  return (
    <Box
      sx={{
        minHeight: 33,
        flexGrow: 1,
        backgroundColor: "white",
        boxShadow: "5px 5px 10px gray",
        borderRadius: "5px",
        overflowY: "scroll",
        overflow: "auto",
        maxHeight: "65%",
      }}
      className="phone-drop"
    >
      <SimpleTreeView>{generatePhonemeItems2(PHONEME_SCHEMA)}</SimpleTreeView>
    </Box>
  );
}

export default PhonemeDropDown;
