import { useState } from "react";

async function request(t, setIpaText) {
  await fetch(
    "https://jp0ob2gb7a.execute-api.us-east-2.amazonaws.com/API/Transliterate",
    {
      method: "POST",
      body: t
    }).then((r) => r.text())
      .then((r) => setIpaText(r))
}

function Translate() {
  const [text, setText] = useState("");
  const [ipaText, setIpaText] = useState("");
  return (
    <span className="container left">
      <div className="content-box left-box">
        <textarea
          className="text-input"
          value={text}
          onChange={(e) => setText(e.target.value)}
          placeholder="Type text here..."
        ></textarea>
      </div>
      <button
        className="text-button"
        onClick={() => text ? request(text, setIpaText) : null}
      >
        Translate to IPA
      </button>
      <div className="content-box left-box">{ipaText}</div>
    </span>
  );
}

export default Translate;
