import Box from "@mui/material/Box";
import { TreeViewBaseItem } from "@mui/x-tree-view/models";
import { RichTreeView } from "@mui/x-tree-view/RichTreeView";
import { SimpleTreeView } from "@mui/x-tree-view/SimpleTreeView";
import { TreeItem } from "@mui/x-tree-view/TreeItem";
import PHONEME_SCHEMA from "./phonemeSchema";

function PhonemeDropDown({ addToSearch }) {
  const generatePhonemeItems = (PHS) => {
    if (typeof PHS === "object") {
      return (
        <TreeItem itemId={PHS.id} label={PHS.id}>
          {PHS.c.map((cc) => {
            return generatePhonemeItems(cc);
          })}
        </TreeItem>
      );
    } else {
      return (
        <TreeItem
          itemId={PHS}
          label={
            <div onClick={(e) => addToSearch("((" + PHS + "))")}>{PHS}</div>
          }
        />
      );
    }
  };
  return (
    <Box
      sx={{
        minHeight: 33,
        flexGrow: 1,
        backgroundColor: "white",
        boxShadow: "5px 5px 10px gray",
        borderRadius: "5px",
        overflowY: "scroll",
        overflow: "auto",
        maxHeight: "65%",
      }}
      className="phone-drop"
    >
      <SimpleTreeView>{generatePhonemeItems(PHONEME_SCHEMA)}</SimpleTreeView>
    </Box>
  );
}

export default PhonemeDropDown;
