import PhonemeDropDown from "./PhonemeDropDown";
import { useState } from "react";

function Search() {
  const [search, setSearch] = useState("");
  const addToSearch = (IPAChar) => setSearch(search + IPAChar);
  return (
    <span className="container right">
      <h1 className="main-title">Poetry Search</h1>
      <h5 className="sub-title">A linguistic search engine by Xander</h5>
      <PhonemeDropDown addToSearch={addToSearch}></PhonemeDropDown>
      <div className="content-box search-input-box">
        <input
          type="text"
          className="search-input"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        ></input>
        <i
          className="fa fa-search"
          style={{
            fontSize: "1.5em",
            cursor: "pointer",
          }}
          onClick={(e) => {}}
          onChange={(e) => console.log(e.target.value)}
        ></i>
      </div>
      <div className="content-box search-results"></div>
    </span>
  );
}

export default Search;
