import { useState } from "react";
import { request } from "../helpers/request";

function Translate() {
  const [text, setText] = useState("");
  const [ipaText, setIpaText] = useState("");
  return (
    <span className="container left">
      <div className="content-box left-box">
        <textarea
          className="text-input"
          value={text}
          onChange={(e) => setText(e.target.value)}
          placeholder="Type text here..."
        ></textarea>
      </div>
      <button
        className="text-button"
        onClick={async () => await fetch({
          url: "https://jp0ob2gb7a.execuamazonaws.com/API/Transliterate",
          method: "POST",
          body: {text}
          })
        .then(e => e.body)
        .then(e => console.log(e.text()))}
      >
        Translate to IPA
      </button>
      <div className="content-box left-box">{ipaText}</div>
    </span>
  );
}

export default Translate;
